INSERT INTO `post` (`id`, `name`, `slug`, `post_category_id`, `image_id`, `text`, `created_at`, `updated_at`) VALUES
(1, 'نماد اعتماد الکترونیک (اینماد) پول زور می‌خواد', 'نماد-اعتماد-الکترونیک-اینماد-پول-زور-می-خواد', 3, 69, '<p>چند سال پیش نماد اعتماد الکترونیک اومد. بهش میگیم اینماد (Enamad)<br />\r\nمثلاً برای این بود که هر سایتی که درگاه پرداخت داره، باید نماد اعتماد بگیره. وگرنه درگاه پرداختش قطع میشه و هر سایت جدیدی که می&zwnj;خواد درگاه بگیره باید اول نماد اعتماد بگیره.</p>\r\n\r\n<p>اون زمان فکر کنم حدود یک ماه تا دو ماه طول کشید که من بتونم برای سایتم نماد اعتماد بگیرم. میرفتم درگاه بگیرم، میگفتن اول باید بری نماد اعتماد بگیری. بعد میرفتم نماد اعتماد بگیرم، میگفتن باید اول درگاه پرداخت داشته باشی که ما بیایم تست کنیم واقعاً توی سایتت پرداخت انجام میشه که بهت درگاه بدیم یا نه. خلاصه کلی مکافات داشتم که تونستم درگاه بگیرم.</p>\r\n\r\n<p>بعد به سایتم که نماد اعتماد داده بودن، هر سال موقع تمدید باز میومدن گیر میدادن که فلان مورد رو رعایت نکردی و &hellip;&nbsp;. هر بار هم میرفتم بهشون میگفتم این موردی بوده که قبلاً باهاتون صحبت کردم و خودتون تأیید کردید و بهم نماد اعتماد دادید. با چند روز تأخیر&nbsp;باز تمدید میکردن.</p>\r\n\r\n<p>بعد از چند سال گفتن که باید هزینه پست که ارسال میشه رو خودتون واریز کنید. گفتیم باشه. واریز میکنیم. بعد مثل اینکه گفتن هر 3 ماه باید احراز هویت بشید. بازم اوکی کردیم. دیگه جدیداً (الان که این پست رو مینویسم، 7 ماهی هست) میگن علاوه بر هزینه پست که میدید، باید 175 هزارتومان هم پرداخت کنید.</p>\r\n\r\n<p>ازشون پرسیدم این 175 هزارتومان برای چی هست دقیقاً؟ چرا باید این هزینه رو پرداخت کنم؟ چرا اصلاً 175 هزارتومان؟ طبق کدوم محاسبات حساب کردین که این 175 هزار تومان رو باید بدیم؟</p>\r\n\r\n<p>جواب درستی به من نمیدن. فقط میگن باید این هزینه رو پرداخت کنی. منم زیر بار حرف زور نمیرم. درگاهی که از سال 90 فکر کنم دارم رو&nbsp;<strong>کنسل</strong>&nbsp;می&zwnj;کنم.&nbsp;<strong>ولی اینماد نمیگیرم.&nbsp;</strong>میرم از این درگاه&zwnj;های واسط می&zwnj;گیرم. بدون دردسر</p>\r\n\r\n<p>حالا اصلاً فررررررررررض کنیم که این 175 هزارتومان هم حقشونه و باید پرداخت کنیم. چرااااااااااااااا باید برای دو تا یا هر چند تا دامنه&zwnj;ای که توی سایت ثبت کردم، از اول همه احراز هویت و پست و هزینه رو پرداخت کنم؟</p>\r\n\r\n<p>مگه همه مشخصات،&nbsp;آدرس،&nbsp;کد پستی،&nbsp;شماره تلفن،&nbsp;شماره موبایل و &hellip;&nbsp;یکی نیستن؟ چرا باید مجدداً هزینه رو بدم؟ دقیقاً اینا برای هر دامنه چه کاری انجام می&zwnj;دن که باید برای هر کدوم از دامنه&zwnj;هام، مبلغ 175 هزارتومان پرداخت کنم؟</p>\r\n\r\n<p>متاسفانه توی کشورمون به جای اینکه کمک کنن که کسب و کارها راه بیفتن، فقط دارن سنگ میندازن و کارها رو سخت می&zwnj;کنن. هزار تا مجوز و &hellip;&nbsp;باید بگیری. هر بار باید هزینه پرداخت کنی و &hellip;&nbsp;.</p>\r\n\r\n<p>همین اینماد هم مثلاً از یه سایتی بخوایم شکایت کنیم، اصلاً پیگیری درستی نمیکنن. یه بار فکر کنم از یه سایتی شکایت کردم، تقریباً هر 1 ماه یه بار یه پیام میومد برام که مثلاً پیگیری میکنن. یه بار اون سایتی که ازش شکایت کرده بودم رو محکوم کرد و بهش فرصت داده بود که تا 3 روز جواب بده. 3 روز شد 5 روز و جواب نداد. بعد دوباره تمدید کرد که فلان روز دیگه وقت داری جواب بدی. بعد اون سایته فکر کنم 1 ماه بعد جواب داد. بعد دوباره تا جواب بعدی هی بهش مهلت میداد و هی 3 بار 3 بار میشد که دیگه خودم خسته شدم. دیگه ادامه ندادم.</p>\r\n\r\n<p>اصلاً نماد اعتماد دقیقاً کارش چیه؟ آیا&nbsp;<strong>ضمانت</strong>&nbsp;می&zwnj;کنه که پولمون رو ندزدن؟ اگه ضمانت نمی&zwnj;کنه، پس دقیقاً چرا این 175 هزارتومان رو داره میگیره؟ پول زوره؟</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>روش کار نماد اعتماد اشتباهه:</h3>\r\n\r\n<p>والا از روز اولی که اومد (همون سال 1389) من گفتم که این روش کاملاً اشتباهه. آخه کاربر عادی از کجا میدونه این صفحه نماد اعتماد واقعیه یا نه؟ خب فوقش منم میتونم برم سایت بسازم. باهاش از مردم پول بگیرم و چیزی بهشون تحویل ندم. تازه عکس نماد اعتماد رو هم توی سایتم بزارم.</p>\r\n\r\n<p>شاید بگید خب وقتی روش کلیک می&zwnj;کنیم یه صفحه&zwnj;ای باز میشه (قبلاً پاپاپ بود) که مشخصات اون سایت و صاحب سایت و آدرس و &hellip;&nbsp;رو نشون میده و می&zwnj;تونیم متوجه بشیم که این سایت مورد اعتماد هست یا نه؟ خب ساخت همون سایت تقلبی مگه کار سختیه؟ این همه درگاه پرداخت تقلبی می&zwnj;سازن و باهاش حساب مردم رو خالی می&zwnj;کنن. صفحه نماد اعتماد که دیگه اصلاً کاری نداره.</p>\r\n\r\n<p>پس روش کار نماد اعتماد کاملاً اشتباهه. راه حل بهتری هست که می&zwnj;شه ارائه داد.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>یکی از اشکالات نماد اعتماد:</h3>\r\n\r\n<p>یکی دیگه از اشکالاتی که نماد اعتماد داره اینه که مثلاً نوع شغل&zwnj;هایی که تعریف نشده براش و جدید هستن، نمی&zwnj;تونن نماد اعتماد بگیرن. وقتی هم بهشون میگیم، میگن هنوز قانونی براش تعریف نشده و &hellip;&nbsp;. پس کلاً شغل&zwnj;های جدید نمی&zwnj;تونن نماد اعتماد بگیرن و &hellip;&nbsp;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>نماد اعتماد اگر پولی میگیره بااااااااااید پاسخگو باشه. اگر وبسایتی دزدی کرد، چون این مجوز داده بهش و باعث شده که ما بهش اعتماد کنیم، باااااااید پاسخگو باشه. باید خسارت ما رو بده. آیا نماد اعتماد حاضر میشه این کار رو بکنه؟ معلومه که نه. تنها کاری که میکنه اینه که یه پیام میده به صاحب اون سایته و میگه جریان چی بوده؟ اون طرف هم جواب نده حدود چند هفته هی بهش مهلت میده هی مهلت میده که شاکی دیگه پشیمون میشه از حقش&hellip; . خب پس چرا نماد داره از ما پول میگیره؟!!! نمیدونم واقعاً</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>من از پشتیبانی نماد اعتماد پرسیدم که این پول رو برای چی میگیرید و چرا باید از اول احراز هویت میکنیم برای دامنه&zwnj;هایی که همه چیزش یکسان هست و ...، تا امروز که این جواب رو دادن بهم.</p>\r\n\r\n<p>* ببینید چقدررررررررررر طول میکشه که یه پیام رو جواب بدن!!! (تاریخ جواب&zwnj;ها رو ببینید. باکس&zwnj;های آبی) این چه خدماتیه که ارائه میدن؟!!!</p>\r\n\r\n<p><img alt=\"متن پیام های نماد اعتماد\" src=\"https://aminbahrami.ir/assets/img/blog/19/screen1.jpg\" /></p>\r\n\r\n<p><img alt=\"پیام های نماد اعتماد\" src=\"https://aminbahrami.ir/assets/img/blog/19/screen2.jpg\" /></p>\r\n\r\n<p><strong>نماد اعتماد بی نماد اعتماد. تماااااااااااام</strong></p>', '2021-02-18 15:47:42', '2021-02-21 05:19:46'),
(4, 'باز نوبت فیلتر اینستاگرام شد', 'باز-نوبت-فیلتر-اینستاگرام-شد', 3, 73, '<p>الان که دارم این پست رو می&zwnj;نویسم، خبرهایی هست که می&zwnj;خوان اینستاگرام رو هم فیلتر کنن!!! اونم به یه دلیل خیییلی مسخره.</p>\r\n\r\n<p>دلیلشون هم این گفتن که تعدادی افراد (سلبریتی&zwnj;ها) به بهانه جمع کردن پول برای مثلاً کمک به زلزله زدگان، افراد بی بضاعت و ...، پول جمع می&zwnj;کنند و برای خودشون استفاده می&zwnj;کنند. اصلاً کاری ندارم که این اتفاق میفته یا نه! ولی آیا واقعاً این دلیل مسخره نیست؟<br />\r\nخب اگه اینطوره دیگه مثلاً نباید چاقویی تولید بشه! چون یه نفر رفته با چاقو آدم کشته!!!<br />\r\nراستی همین سلبریتی&zwnj;ها از اینترنت هم استفاده کردن که تونستن وارد اینستاگرام بشنااا. فکر کنم اینترنت هم باید کلاً قطع بشه. چون بعضی&zwnj;ها با همین اینترنت رفتن وارد شبکه&zwnj;های اجتماعی شدن و کارای خلاف و کارایی که شما دوست ندارید انجام دادن!!! (هر چند که واقعاً دور از انتظار نیست که همین ادعا رو هم بکنن که کلاً اینترنت باید قطع بشه!)</p>\r\n\r\n<p>آقای معاون اول دادستان کل کشور، شما خودتون به این بهونه&zwnj;ای که آوردید فکر کنید! از نظر خودتون مسخره نیست؟ شما اگه ادعا می&zwnj;کنید که کلاهبرداری شده، چرا اقدام نمی&zwnj;کنید؟ اگر می&zwnj;دونید که کلاهبرداری شده و کاری نمی&zwnj;کنید، پس شما کوتاهی کردید و وظیفه&zwnj;ی خودتون رو انجام ندادید.<br />\r\nولی اگر نمی&zwnj;دونید که کلاهبرداری شده و فقط بهونه آوردید، پس تهمت زدین و باز هم مقصر هستید. پس سلبریتی&zwnj;ها باید ازتون شکایت کنند که بهشون تهمت زدید.</p>\r\n\r\n<p>اولش فکر می&zwnj;کردم که معاون اول دادستان، این حرف رو نزده. ولی رفتم که مطمئن بشم؛ دیدم توی سایت فارس نیوز هم این خبر وجود داره که این حرف زده شده.<br />\r\nاینم لینک خبر توی فارس نیوز (البته اگه خبر رو پاک نکنن):<br />\r\n<a href=\"https://www.farsnews.com/news/13970412001091/\" target=\"_blank\">https://www.farsnews.com/news/13970412001091/</a></p>\r\n\r\n<p>یکی دیگه از دلایلشون هم اینه که اینستاگرام نقش آشکاری در ایجاد آشوب و اعتشاشات و ... داره. خب شما به جای اینکه نذارید مردم مشکلی داشته باشن که مثلاً به اعتراض و آشوب و ... کشیده بشه، دارید اینستاگرام رو فیلتر می&zwnj;کنید که مردم، دیگه راحت&zwnj;تر متوجه اعتراض بقیه نشن و مثلاً کنترل کنید. ولی این اشتباهه.</p>\r\n\r\n<p><strong>اگر دادستانی، دولت و ... اینقدر که برای فیلترینگ و اذیت کردن مردم دارن تلاش می&zwnj;کنن، برای مشکلات کشور تلاش می&zwnj;کردن، الان وضع کشور این نبود. البته اگر اصلاً مشکلات کشور براشون مهم باشه.</strong></p>\r\n\r\n<p>من دارم اینطوری فکر می&zwnj;کنم، هر چیزی که مردم رو از مشکلات، دزدی&zwnj;ها (زشته؛ نگیم دزدی. بگیم اختلاس) و خیانت&zwnj;ها و ... باخبر می&zwnj;کنه رو دارن فیلتر می&zwnj;کنند که مردم از هیچ چیزی خبر نداشته باشن تا با خیال راحت این کارها رو انجام بدن. هر چند مردم هم بدونن براشون مهم نیست!</p>\r\n\r\n<p>آقای آذری جهرمی هم حتی بعد از خبر فیلتر اینستاگرام، یه توییتی رو ارسال کردن:</p>\r\n\r\n<blockquote>\r\n<p dir=\"rtl\">موضوع پیگیری برخی برای &quot;فیلتر&quot; سرویسهای مختلف اینترنتی تازگی ندارد. &quot;رفع فیلتر&quot; توئیتر در صف انتظار جلسه کارگروه تعیین مصادیق قرار دارد. آنچه تازگی دارد طرح یک موضوع قدیمی توسط برخی رسانه&zwnj;ها برای انحراف افکار عمومی از مطالبه&zwnj;گری&nbsp;<a href=\"https://twitter.com/hashtag/%D8%B4%D9%81%D8%A7%D9%81_%D8%B3%D8%A7%D8%B2%DB%8C?src=hash&amp;ref_src=twsrc%5Etfw\" target=\"_blank\">#شفاف_سازی</a>&nbsp;سوءاستفاده کنندگان ارزی اخیر است</p>\r\n&mdash; MJ Azari Jahromi (@azarijahromi)&nbsp;<a href=\"https://twitter.com/azarijahromi/status/1014345781233545217?ref_src=twsrc%5Etfw\" target=\"_blank\">مشاهده</a></blockquote>\r\n\r\n<p>شما اگر جایگزین مطمئن (مطمئن مطمئن مطمئن) برای اینستاگرام، تلگرام و ... معرفی کنید، من خودم به شخصه از هیچ کدومش استفاده نمی&zwnj;کنم و از جایگزینش که مال کشور خودمونه استفاده می&zwnj;کنم.</p>\r\n\r\n<p>ولی مشکل جای دیگه&zwnj;ای هست. اکثر مردم به شما (دولت، قوه قضائیه، ...) اعتماد ندارند. وگرنه اینقدر تبلیغات سنگین که می&zwnj;کردید که مثلاً از سروش استفاده کنید و ...، باید جواب میداد. صحبت قوه قضائیه شد؛ من خودم اصلاً دید مثبتی به قوه قضائیه ندارم.</p>\r\n\r\n<p>اصلاً من حاضرم با نصف این هزینه&zwnj;ای که شما برای تبلیغاتتون انجام می&zwnj;دید کاری کنم که مردم واقعاً تمایل داشته باشند از پیام رسان&zwnj;ها و شبکه&zwnj;های اجتماعی ایرانی استفاده کنند. اصلاً از کالای ایرانی استفاده کنند و افتخار کنند که از کالای ایرانی استفاده می&zwnj;کنند. (ولی با تغییرات)</p>\r\n\r\n<p>البته من توی این مسئولانی که کمی میشناسمشون، &laquo;یه کم&raquo; آقای جهرمی رو قبول دارم. اگر محدودیت نداشت، فکر کنم کارهای خیلی بهتری انجام می&zwnj;داد. دلیل اینکه میگم یه کم، اینه که آدما در ظاهر یه چیزی هستن ولی در باطن یه آدم دیگه هستن. انشاالله که ایشون اینطوری نباشن.</p>\r\n\r\n<p>همچنان میگم که مشکل مردم اعتماده. یه فکری به حال اعتماد مردم کنید تا اتحاد واقعی رو ببینید. با این کاراتون فقط دارید مردم رو از خودتون دور می&zwnj;کنید و جبرانش بسیار بسیار سخت خواهد بود.</p>', '2021-02-18 16:55:54', '2021-02-21 05:19:35'),
(5, 'فارسی سازی بوت استرپ 3 و 4', 'فارسی-سازی-بوت-استرپ-3-و-4', 4, 74, '<p>برنامه نویس&zwnj;های وب که از بوت استرپ استفاده میکنن، مطمئناً با این مشکل روبرو بودن که برای قالب&zwnj;های فارسی (راست به چپ) مشکل داشتن. چون ساختار اصلی بوت استرپ، چپ به راست هست و برای سایت&zwnj;های غیر فارسی یا سایت&zwnj;های چپ به راست مناسب بود.</p>\r\n\r\n<p>توی اینترنت اگر سرچ کرده باشید، افرادی اومدن بوت استرپ رو راست به چپ کردن و گذاشتن برای دانلود. حتی پیش اومده که میفروشنش!!!</p>\r\n\r\n<p>ولی طبق تجربه خودم، همه اونا که دیدم و تستشون کردم، مشکل داشتن و درست کار نمیکردن و دردسر داشتیم باهاشون.</p>\r\n\r\n<p>میخوام به شما روش فارسی سازی رو توی کمتر از یک دقیقه آموزش بدم. من همیشه برای پروژه&zwnj;هایی که می&zwnj;نویسم، از همین روش استفاده می&zwnj;کنم و فارسی سازی میکنم. تا الان هم هیچ مشکلی نداشتم و خیلی قشنگ و نرمال داره کار میکنه. به نظر خودم که مطمئن ترین روش همینه. نیازی هم نیست که از سایت&zwnj;های دیگه دانلود کنیم و یا اینکه حتی خریداری کنیم.</p>\r\n\r\n<p>این روش همیشه کار میکنه. فرقی نمیکنه که نسخه 3 بوت استرپ باشه یا نسخه 4 که جدیداً اومده. در هر صورت جواب میده.</p>\r\n\r\n<p>خب. برای اینکه کیفیت آموزش بهتر باشه و بصورت عملی هم کار کنیم، تصمیم گرفتم که فیلم آموزش انجام این کار رو ضبط کنم. شما توی ویدئوی پایین، روش کار رو میتونید ببینید. اگر سوالی داشتید توی کامنت&zwnj;ها بپرسید. حتماً جواب میدم. (البته اگر بلد باشم)<br />\r\nتوی این ویدئو، نحوه دانلود، کامپایل و فارسی سازی (یا همون راست به چپ کردن) بوت استرپ رو آموزش میدم. حتی بهینه سازی بوت استرپ رو برای پروژه&zwnj;هامون توضیح میدم که سایت&zwnj;های سبک&zwnj;تری داشته باشیم.</p>\r\n\r\n<p>اگه دیدین کیفیتش پایینه، بزارید روی کیفیت بالاتر (گوشه پایین سمت راست). چون آپارات مثل اینکه پیش فرض داره با کیفیت 360 نشون میده که زیاد جالب نمیشه.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><iframe height=\"100%\" src=\"https://www.aparat.com/video/video/embed/videohash/WJqiE/vt/frame\" title=\"فارسی سازی بوت استرپ در یک دقیقه\" width=\"100%\"></iframe></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>البته این ویدئو رو قرار بود خیلی زودتر قرار بدم و دوستان هم خیلی پیگیری کردن. ولی خب درگیری&zwnj;ها زیاد بود. یکیش اینکه لپ تاپم خراب شد و تقریباً دو ماه گارانتی بود :-) به لپ تاپ ضعیف دیگه&zwnj;ای داشتم که ضبط صدا باهاش خیلی نویز داشت.<br />\r\nدیگه ببخشید دیر شد :-)</p>', '2021-02-18 16:57:43', '2021-02-21 05:19:26'),
(6, 'دنبال گرافیست خوب می‌گردی؟', 'دنبال-گرافیست-خوب-می-گردی', 5, 76, '<p>اول یه توضیحی کوچیک بدم، بعد یه گرافیست خیلی کار درست رو بهتون معرفی می&zwnj;کنم :-)</p>\r\n\r\n<p>برنامه نویس&zwnj;ها اگه خودشون بخوان تنهایی کار کنن، همون بهتر که کار نکنن :-D منظورم اینه که اگه خودشون میخوان گرافیک رو بزنن و خودشون هم کدنویسی کنن کاره خوب در نمیاد. دیگه هر کسی جایی تخصص و یا استعداد داره. کار رو باید داد دست متخصصش.</p>\r\n\r\n<p>بازدیدکنندگان سایت یا اپلیکیشن و ... فقط ظاهر رو می&zwnj;بینن. در اصل UI و UX برای کاربر مهمه. کاری ندارن که اون سایت یا اپلیکیشن و ... چقدر حرفه&zwnj;ای برنامه نویسی شده و ... .<br />\r\nپس اگر می&zwnj;خوایم که کاربرا جذب بشن، باید به UI و UX توجه خیلی زیادی کنیم.</p>\r\n\r\n<p>بعضی از مشتری&zwnj;ها (شخص یا شرکت و ...) که برای پروژه تماس میگیرن، به من میگن خودت گرافیک رو بزن. در اصل می&zwnj;خوان که گرافیک رو از سایت&zwnj;های دیگه کپی کنم و ... . میگن با سلیقه خودت. من که هیچ وقت این کار رو قبول نمی&zwnj;کنم. چون اگه با سلیقه خودم بزنم که کلاً همه فراری میشن از سایت یا اپلیکیشنه :-D</p>\r\n\r\n<p>همیشه میگم اگر خودتون گرافیست سراغ دارید، بدید طراحی کنه. اگه نه که خودم یه گرافیست حرفه&zwnj;ای و کار درست رو بهتون معرفی می&zwnj;کنم. بدید که براتون طراحی کنه. کدنویسی هم اگه خواستید با من.</p>\r\n\r\n<p>آقوی&nbsp;<a href=\"https://gnoori.com/\" target=\"_blank\">حسین قاضی نوری</a>، یه رفیق و گرافیست خیلی خوبیه که کارش حرف نداره. من مشتری&zwnj;هایی که داشتم رو دیگه می&zwnj;فرستم پیش خودش و تا الان که همه راضی بودن :-) اکثراً هم همون بار اول گرافیک رو تأیید می&zwnj;کنن و حتی نیازی نمیبینن که بخوان جاییش رو تغییر بدن. گرافیک، المنت&zwnj;ها، انتخاب رنگ&zwnj;ها، UX و ... رو همیشه از آخرین روش&zwnj;هایی که معرفی شده (استانداردهای جهانی) استفاده می&zwnj;کنه و گرافیکی به روز و عالی طبق سناریویی که بهش میگید، انجام میده.</p>\r\n\r\n<p>کارهایی که من خبر دارم انجام میده توی بحث گرافیک اینا هستن:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>طراحی اپلیکیشن</li>\r\n	<li>طراحی سایت</li>\r\n	<li>طراحی لوگو</li>\r\n	<li>طراحی بنر و پوستر</li>\r\n	<li>و ...</li>\r\n	<br />\r\n	<li>چیزای دیگه&zwnj;ای هم هست که می&zwnj;دونم کار می&zwnj;کنه، ولی من سر در نمیارم. ازش می&zwnj;پرسم، بعد اینجا اضافه می&zwnj;کنم<br />\r\n	آدرس سایتش هم اینه.&nbsp;<a href=\"https://gnoori.com/\" target=\"_blank\">https://gnoori.com</a><br />\r\n	کار زیاد انجام داده. ولی خب هنوز همشو توی سایتش نذاشته.</li>\r\n</ul>', '2021-02-18 17:01:24', '2021-02-21 05:19:06'),
(7, 'درآمد برنامه نویسان چقدره؟', 'درآمد-برنامه-نویسان-چقدره', 5, 77, '<p>افرادی که برنامه نویسی میکنن، به احتمال خیلی زیاد ازشون سوال شده که برنامه نویسی چقدر درآمد داره؟ آیا ارزش داره که براش وقت بزاریم و یاد بگیریم؟ پول توشه؟ :-)<br />\r\nعنوان پست شاید مرتبط با متن پست نباشه. چون خیلی ها سرچ میکردن، تصمیم گرفتم اینو بزارم برای عنوانش که بعد بیان اینو بخونن و ارزش کارشون رو نیارن پایین. شاید عنوان بهتر براش این باشه:&nbsp;<strong>ارزش برنامه نویس</strong></p>\r\n\r\n<p>همونطور که قبلاً هم گفتم، برنامه نویسی علاقه میخواد. علاقه مهمترین چیزه.<br />\r\nخب. با فرض اینکه قبلاً این پست رو خوندین (<a href=\"https://aminbahrami.ir/blog/5-%DA%86%DA%AF%D9%88%D9%86%D9%87-%D8%A8%D8%B1%D9%86%D8%A7%D9%85%D9%87-%D9%86%D9%88%DB%8C%D8%B3-%D8%AD%D8%B1%D9%81%D9%87-%D8%A7%DB%8C-%D8%B4%D9%88%DB%8C%D9%85.html\" target=\"_blank\">چگونه برنامه نویس شویم؟</a>)، پس دیگه اونا رو توضیح نمیدم و مطالب قبلی رو تکرار نمیکنم.</p>\r\n\r\n<p>من میتونم بگم که درآمد برنامه نویسی خیلی خوبه (با توجه به ارزش کاری که انجام میدیم). ولی تحت شرایطی.</p>\r\n\r\n<p>مثلاً چند سال پیش یه نفر تماس گرفت و پروژه&zwnj;ای رو می&zwnj;خواست که براش انجام بدم.<br />\r\nمنم با بررسی داکیومنت و نیازهای پروژه، بهش زمان و قیمت رو دادم.<br />\r\nقیمتی که چند سال پیش دادم با توجه به زمانی که از من میگرفت، 16 میلیون تومن بود.<br />\r\nبعد گفت قیمت بالا هست و رفت داد یه برنامه نویس دیگه&zwnj;ای براش این کار رو انجام داد.<br />\r\nشاید باورتون نشه، اون برنامه نویسه گفته بود که براش انجام میده، اپلیکیشن اندروید و iOS هم براش میسازه و خیلی کارهای دیگه؛ با قیمت 450 هزارتومان!!!<br />\r\nالبته خب کیفیت کار متفاوته. نمیشه گفت همون کاری که من انجام میدادم رو اون برنامه نویسه انجام داده براش.</p>\r\n\r\n<p>یا اینکه یه بار یه نفر پروژه&zwnj;ای می&zwnj;خواست؛ من بهش یه قیمتی رو دادم. گفت که زیاده. رفت هزینه کرد و داد یه برنامه نویس دیگه، تقریباً با نصف قیمتِ من انجام داد براش. ولی بعد از یکسال برگشت و گفت خودت برام بنویس و از کاری که اون برنامه نویسه انجام داده بود، راضی نبود. دوباره هزینه کرد. منم پروژه رو براش نوشتم و کاملاً هم راضی بود. حتی بعد از گذشت چندین سال، هنوز پروژه&zwnj;ای نیاز داشته باشه، میفرسته که خودم انجام بدم. حتی اگر بگم وقت ندارم و 2-3 ماه بعد وقت میکنم که کارتو شروع کنم، باز منتظر میمونه.</p>\r\n\r\n<p>متاسفانه برنامه نویس&zwnj;ها ارزش کار خودشون رو میارن پایین. ولی من هیچ وقت حاضر نمیشم برای اینکه پروژه&zwnj;ای رو بگیرم، ارزش کار خودمو بیارم پایین.</p>\r\n\r\n<p>یه بار همینطوری یه سایت فری لنس رو داشتم چک میکردم. دیدم یه نفر یه پروژه کوچیکی رو میخواد. برنامه نویس&zwnj;های زیادی درخواست داده بودن که توی مثلاً 3 روز یا 5 روز یا فلان روز کار رو تحویل میدن. خب مشکلی نیست. ولی اگر بهتون بگم با چه قیمت&zwnj;هایی انجام میدن، واقعاً تعجب می&zwnj;کنید!!! یکیشون گفته بود 3 روزه انجام میده، با قیمت 5 هزار تومان!!!!!!!!!!!!!! نمیدونم واقعاً چه ارزشی براش داره که 3 روز وقت بزاره!!!<br />\r\nاصلاً بگیم یکساعت وقت بزاره. 5 هزار تومان؟ چرا آخه؟</p>\r\n\r\n<p>برنامه نویس واقعی هیچ وقت نباید ارزش کار خودشو پایین بیاره فقط برای اینکه یه پروژه بگیره.</p>\r\n\r\n<p><br />\r\n<strong>درآمد برنامه نویس&zwnj;ها</strong></p>\r\n\r\n<p>شغل برنامه نویسی (یا از نظر من علاقه برنامه نویسی) یکی از شغل&zwnj;های پر درآمد دنیا هست. اگر کار برنامه نویس خوب باشه، همیشه کار براش وجود داره.<br />\r\nولی خب همیشه برنامه نویسی انجام دادن خوب نیست. به نظر من برنامه نویسی علاقه هست و همیشه باید دنبال علاقمون باشیم. ولی اگر بخوایم همیشه به عنوان منبع درآمد بهش نگاه کنیم، خوب نیست. چون بعد از اینکه پیرتر میشیم، شاید کششِ جوونی رو نداشته باشیم که بتونیم مثل قبل برنامه نویسی انجام بدیم. برنامه نویسی تمرکز میخواد، آرامش میخواد و ... . نباید که همیشه برنامه نویسی انجام داد. همیشه باید چیزای جدیدتر یاد بگیریم.<br />\r\nباید به این فکر کنیم که ایده یا پروژه&zwnj;ای رو راه اندازی کنیم که خودش درآمدزا باشه و درآمد همیشگی داشته باشیم.<br />\r\nنه فقط یه پروژه؛ بلکه چندین ایده یا پروژه رو باید انجام بدیم.</p>\r\n\r\n<p>یه بار یه نفر پروژه&zwnj;ای میخواست که براش انجام بدم.<br />\r\nمیخواست از برنامه نویس قبلیه بگیره و بده من براش انجام بدم.<br />\r\nدیگه داشتم با برنامه نویسه صحبت میکردم و اونم سوال&zwnj;هایی می&zwnj;کرد که ببینه من تخصص کافی رو دارم یا نه :-)<br />\r\nبعد چیزایی که کار کرده بودم رو بهش گفتم. اونم اومد سایتمو چک کرد.<br />\r\nگفت اگر اومده بودین توی شرکت ما استخدام بشید، من قسمت &laquo;دانش&zwnj;ها و تخصص&zwnj;های من&raquo; که توی سایتت گذاشتی رو میدیدم، سریع شما رو رد می&zwnj;کردم :-) چون چیزای زیادی نوشتین و یه نفر نمیتونه همه اینا رو یاد بگیره.<br />\r\nاین چیزایی که نوشتم چیزای زیادی نیستن. مواردی هستن که باید اینا رو بلد باشیم و وقت بزاریم برای یادگیریش.<br />\r\nمنم بهشون گفتم اونی که میاد توی شرکت شما کار کنه، برای درآمد کارمندی میاد و چیزای کمی بلده. اونی که همه این چیزا رو بلده حاضر نمیشه اصلاً بیاد بصورت کارمندی کار کنه. چون همینطوری درآمدش زیاده و نیاز نداره که توی یه شرکت بصورت کارمندی کار کنه :-)</p>\r\n\r\n<p>کلاً به نظرم برنامه نویس حرفه ای نباید بصورت کارمندی کار کنه. نباید درگیر روزمرگی بشه و کارهای تکراری انجام بده. باید همیشه یاد بگیره و پیشرفت کنه.</p>\r\n\r\n<p>دوستانِ برنامه نویس. ارزش کار خودتون رو نیارید پایین.<br />\r\nکیفیت کارتون رو بالا ببرید و حاضر نشید ارزشتونو پایین بیارید.</p>', '2021-02-18 17:02:28', '2021-02-21 05:18:53');

