INSERT INTO `customer` (`id`, `name`, `from`, `text`, `created_at`, `updated_at`) VALUES
(1, 'عادل اخکندی', 'مدیر سایت فرا کنکور', 'بنده از آقای بهرامی کمال تشکر را دارم که همواره کنار ما بودند و با صبر و حوصله بسیار، به بررسی مشکلات و بهبود فعایل سایت ما پرداختند و همیشه همکاری صمیمانه‌ای را با ما داشته‌اند.', '2021-03-06 20:27:04', '2021-03-06 20:27:04'),
(2, 'دکتر خلفی نژاد', 'بخش شیمی دانشگاه شیراز', 'بدین وسیله از حضرتعالی که با نهایت تلاش و پشتکار در راستای تامین نیازهای مطرح شده در خصوص سامانه‌ی «شصتمین سال بنیانگذاری بخش شیمی و سی‌امین سال راه اندازی دوره‌ی دکتری دانشگاه شیراز» و همچنین همکاری صمیمانه و صادقانه‌ای که در جهت ارائه‌ی خدمات فنی مورد نیاز این مجموعه داشته‌اید، تقدیر و تشکر به عمل می‌آید؛ بدین وسیله رضایت کامل خود را ارائه خدمات فنی کامل براساس نیازهای این مجموعه از سوی شما را اعلام می‌داریم.', '2021-03-06 20:28:06', '2021-03-06 20:28:06'),
(3, 'فرشید', 'کالیفرنیا', 'بنده از کیفیت و سرعت کار آقای بهرامی بسیار راضی هستم و همیشه به بهترین شکل ممکن پروژه‌های ما را انجام داده‌اند. پروژه‌هایی که برای شرکت‌های برنامه نویسی در انگلیس دو ماه طول می‌کشید، آقای بهرامی با سرعت بسیار زیاد و با کیفیت بسیار عالی، در کمتر از 3 هفته انجام داده‌اند.', '2021-03-06 20:28:30', '2021-03-06 20:28:30');

